            }
        },
        mutations: {
            inc: (state) => {
                return state.count++;
            },
            mes: (state, message) => {
                return state.messages.push(message);
            },
            users: (state, users) => {
                state.users = users.data;
                return state.users;
            }
        }
    });
}
